// NODE MODULES
import { useState } from 'react';
import './index.css';
import { useNavigate } from 'react-router-dom';
// MY COMPONENTS
import LoginSection from '../../screens/login-page/login-section';
import ShowCartList from '../show-cart-list';
import BlurConfig from './blur-config';

const Navigation = (props) => {
    let [isLoginOn, setLogin] = useState(false);

    let [cart, setCart] = useState({
        showCart: false,
        cartQuantity: 0
    });

    let navigate = useNavigate();
    const directTo = (id) => {
        navigate(`${id}`);
    }

    return (
        <>
            <header>
                <nav>
                    <img className='logo-img' src={process.env.PUBLIC_URL + '/navigation-images/logo.png'} alt='Site Logo' onClick={() => directTo('/')}></img>
                    <div className='create-button'>
                        {
                            props.button.map((element) => {
                                return (
                                    <>
                                        <button onClick={() => {
                                            setCart( (prevCart) => ({...prevCart, showCart : false}) );
                                            setLogin(!isLoginOn);
                                            return element.redirect ? directTo(`${element.routeId}`) : null
                                        }}>
                                            <div>
                                                {element.img != null ? <img src={element.img} alt='User Icon'></img> : null}
                                                {element.name}
                                            </div>
                                        </button>
                                    </>
                                )
                            })
                        }
                    </div>
                    <div className='cart-button'>
                        <button onClick={() => {
                            setCart( (prevCart) => ({...prevCart, showCart : !cart.showCart}) );
                            setLogin(false);
                        }}>
                            <div>
                                <p>{cart.cartQuantity}</p>
                            </div>
                            <p>Cart</p>
                        </button>
                    </div>
                </nav>
            </header>
            <button onClick={() => setCart( (prevCart) => ({...prevCart, cartQuantity : cart.cartQuantity+1}))}>Add pizza</button>
            <BlurConfig loginState={isLoginOn}/>
            {isLoginOn ? <LoginSection /> : null}
            {cart.showCart ? <ShowCartList quant={cart.cartQuantity}/> : null}
        </>
    )
}

export default Navigation;
