import Nav from '../../../src/components/navigation'
import Footer from '../../components/footer';
import './index.css'
import { useState, useEffect } from 'react';

class linkButton {
    constructor(routeId, name, img, redirect) {
        this.routeId = routeId;
        this.name = name;
        this.img = img;
        this.redirect = redirect;
    }
}

let buttonList = [];
//process.env.PUBLIC_URL + '/navigation-images/user-profile-icon.png'
buttonList.push(new linkButton('/login', <p>Account</p>, null, false))
buttonList.push(new linkButton('/menu', <p>Menu</p>, null, true))
buttonList.push(new linkButton('/', <p>Stores</p>, null, false))
buttonList.push(new linkButton('/', <p>Promotions</p>, null, false))

const LandingPage = () => {
    const [test, setTest] = useState(-50);

    const setter = () => {
        setTest(window.scrollY - document.getElementById('vid').offsetHeight);
    }
    const setter2 = () => {
        console.log("Resized")
            if(window.innerWidth < 800){
                setVideo('/navigation-images/1video-banner-mobile.mp4');
            }
            else{
                setVideo('/navigation-images/1video-banner.mp4')
            }
    }

    useEffect(() => {
        document.addEventListener("scroll", setter);

        document.addEventListener("resize", setter2);

        return () => {
            document.removeEventListener("scroll", setter)
        }
    }, [])

    let [video, setVideo] = useState('');

    return (
        <>
            <Nav button={buttonList} test={test} />
            <main>
                <video id='vid' autoPlay muted loop
                    src={process.env.PUBLIC_URL + video}>
                </video>
                <h2>Promotions Of The Day</h2>
                <div className='images-container'>
                    <img id='ad0' alt='ad0'></img>
                    <aside className='aside-image-container'>
                        <img id='ad1' alt='ad1'></img>
                        <img id='ad2' alt='ad2'></img>
                    </aside>
                    <img id='ad3' alt='ad3'></img>
                </div>
            </main>
            <footer>
                <Footer />
            </footer>
        </>
    )
}

export default LandingPage;
