import './index.css'
import Navigation from "../../components/navigation";

const Menu = () => {
    class linkButton{
        constructor(routeId, name, img, redirect){
            this.routeId = routeId;
            this.name = name;
            this.img = img;
            this.redirect = redirect;
        }
    }
    
    let buttonList = [];
    //process.env.PUBLIC_URL + '/navigation-images/user-profile-icon.png'
    buttonList.push(new linkButton('/login', <p>Account</p>, null, false))
    buttonList.push(new linkButton('/', <p>Stores</p>, null, false))
    buttonList.push(new linkButton('/', <p>Promotions</p>, null, false))

    return(
        <>
            <Navigation button={buttonList}/>
            <main>
                <div className="menu-title">
                    <h1>Card√°pio</h1>
                </div>
                <div className='option-container'>
                    <div className='option'>
                        <img src='https://kitchenatics.com/wp-content/uploads/2020/09/Cheese-pizza-1.jpg' alt='cheese pizza'></img>
                        <div className='option-info'>
                            <h3>Cheese Pizza</h3>
                            <p>Description Description</p>
                            <h4>$15.00</h4>
                            <button>Add To Cart</button>
                        </div>
                    </div>
                    <div className='option'>
                        <img src='https://media.istockphoto.com/id/178416773/photo/some-glasses-with-strawberry-liqueur.jpg?s=612x612&w=0&k=20&c=wo7eqLKBOV-FQZ15Lo7A3RMLPAdvwbkVPqlZm_GM9pc=' alt='strawberry juice'></img>
                        <div className='option-info'>
                            <h3>Strawberry Juice</h3>
                            <p>Description Description</p>
                            <h4>$7</h4>
                            <button>Add To Cart</button>
                        </div>
                    </div>
                </div>
            </main>
        </>
    )
}

export default Menu;
