            <div className='another-links'>
                <ul>
                    {footerLinksArray.map((element, i) => {
                        return <React.Fragment key={i}>
                            <li>
                                <a href={element.url}>
                                    {element.linkName}
                                </a>
                            </li>
                        </React.Fragment>
                    })}
                </ul>
            </div>
            <div>
                <figure className='partner-logo'>
