import './index.css';
import { useState } from 'react';
import Navigation from '../navigation';

const LoginSection = () => {
    let [isLoginOn, setLogin] = useState(false);

    const updateLoginState = (newState) => {
        setLogin(newState);
    };

    <Navigation updateLoginState={updateLoginState} loginState={isLoginOn}/>

    return(
        <>
            
            <div className='container'>
                <figure className='close-icon'>
                    <img src={process.env.PUBLIC_URL + '/navigation-images/close-icon.png'} alt='close login' onClick={() => {
                        
                    }}></img>
                </figure>
                <form className='input-label'>
                    <label>
                        <h1>Login</h1>
                        <input type="text" name="name" placeholder='Name'/>
                    </label>
                </form>
            </div>
        </>
    )
}

export default LoginSection;
