import { useState } from 'react';
import './index.css';
import { useNavigate } from 'react-router-dom';
import LoginSection from '../../screens/login-page/login-section';

const Navigation = () => {
    let [blur, setBlur] = useState(true);
    const cuo = () => {
        if(blur){
            // document.body..classList.add('blur-all');
            document.querySelector('header').classList.add('blur-all')
        }
        else{
            document.header.classList.remove('blur-all');
        }
        console.log(blur);
        setBlur(!blur)
    }

    let navigate = useNavigate();
    const bla = (id) => {
        // navigate(`${id}`);
    }
    
    return(
        <>
            <header>
                <nav>
                    <img className='logo-img' src='/navigation-images/logo.png' alt='Site Logo' onClick={() => bla('/')}></img>
                    <div className='create-button'>
                        <button onClick={() => {
                            bla('/login');
                            cuo();
                        }}>
                            <div>
                                <img src='/navigation-images/user-profile-icon.png' alt='User Icon'></img>
                                <p>Fazer Login</p>
                            </div>
                        </button>
                        <button>
                            <div>
                                <img src='/navigation-images/user-profile-icon.png' alt='User Icon'></img>
                                <p>Criar Conta</p>
                            </div>
                        </button>
                    </div>
                </nav>
            </header>
            {blur ? null : <LoginSection/>}
        </>
    )
}

export default Navigation;
