import { useState } from 'react';

const Menu = () =>{
    let [showMenu, setShowMenu] = useState(false);

    return(
        <div className='menu'>
            <img src={process.env.PUBLIC_URL + '/navigation-images/menu_icon.png'} alt='menu icon' onClick={() => {
                const element = document.querySelector('.sub-nav');
                if(!showMenu){
                    element.classList.remove('display-none');
                    document.querySelector('header').classList.add('solid-header')
                    document.querySelector('body').classList.add('hide-scroll')
                    setShowMenu(true);
                }else{
                    element.classList.add('display-none');
                    setShowMenu(false);
                    document.querySelector('header').classList.remove('solid-header')
                }
            }}></img>
        </div>
    )
}

export default Menu;
