import { useState } from "react";


const ScrollFunc = () => {
    const [windowHeight, setWindowHeight] = useState(0);

    const setHeight = () => {
        setWindowHeight(window.scrollY);
    }

    document.addEventListener("scroll", setHeight);
}

export default ScrollFunc;
