import './index.css'
import Navigation from "../../components/navigation";
import Footer from '../../components/footer';
import MenuOptions from './menu-options';
import { useState } from 'react';

const Menu = () => {
    class linkButton {
        constructor(routeId, name, img, redirect) {
            this.routeId = routeId;
            this.name = name;
            this.img = img;
            this.redirect = redirect;
        }
    }

    fetch(process.env.PUBLIC_URL + '/api/products.json')
            .then(response => response.json())
            .then(data => {
                console.log(data.name); // John Doe
                console.log(data.age); // 30
                console.log(data.email); // johndoe@example.com
                })
            .catch((err) => {console.log(err)});

    let [categorySelected, setCategory] = useState('null');

    let buttonList = [];
    //process.env.PUBLIC_URL + '/navigation-images/user-profile-icon.png'
    buttonList.push(new linkButton('/login', <p>Account</p>, null, false))
    buttonList.push(new linkButton('/', <p>Stores</p>, null, false))
    buttonList.push(new linkButton('/', <p>Promotions</p>, null, false))

    let categorySelectElement = <div className='food-category'>
        <div className='food-container' onClick={() => {
            setCategory('pizza');
        }}>
            <h2>Pizza</h2>
        </div>
        <div className='food-container' onClick={() => {
            setCategory('drink');
        }}>
            <h2>Drink</h2>
        </div>
        <div className='food-container'>
            <h2>Sandwich</h2>
        </div>
        <div className='food-container'>
            <h2>Burguer</h2>
        </div>
    </div>

    return (
        <>
            <Navigation button={buttonList} />
            <main>
                <div className="menu-title">
                    <h1>Menu</h1>
                </div>
                {categorySelected === 'null' ? <>{categorySelectElement}</> : null}
                {/* <MenuOptions/> */}
                {categorySelected !== 'null' ? <MenuOptions categorySelected={categorySelected} setCategory={setCategory}/>
                    : null}
            </main>
            <Footer />
        </>
    )
}

export default Menu;
