// NODE MODULES
import { useState, useEffect } from 'react';
import './index.css';
import { useNavigate } from 'react-router-dom';
// MY COMPONENTS
import LoginSection from '../../screens/login-page/login-section';
import ShowCartList from '../show-cart-list';

const Navigation = (props) => {
    let [blur, setBlur] = useState(false);
    useEffect(() => {
        if (blur) {
            document.querySelector('header').classList.add('blur-all');
            document.querySelector('main').classList.add('blur-all');
            document.querySelector('footer').classList.add('blur-all');
            setCart( (prevCart) => ({...prevCart, showCart : false})
            );
        } else {
            document.querySelector('header').classList.remove('blur-all');
            document.querySelector('main').classList.remove('blur-all');
            document.querySelector('footer').classList.remove('blur-all');
        }
    }, [blur]);

    let [cart, setCart] = useState({
        showCart: false,
        cartQuantity: 0
    });

    let navigate = useNavigate();
    const directTo = (id) => {
        navigate(`${id}`);
    }

    return (
        <>
            <header>
                <nav>
                    <img className='logo-img' src={process.env.PUBLIC_URL + '/navigation-images/logo.png'} alt='Site Logo' onClick={() => directTo('/')}></img>
                    <div className='create-button'>
                        {
                            props.button.map((element) => {
                                return (
                                    <>
                                        <button onClick={() => {
                                            setBlur(!blur)
                                            return element.redirect ? directTo(`${element.routeId}`) : null
                                        }}>
                                            <div>
                                                {element.img != null ? <img src={element.img} alt='User Icon'></img> : null}
                                                {element.name}
                                            </div>
                                        </button>
                                    </>
                                )
                            })
                        }
                    </div>
                    <div className='cart-button'>
                        <button onClick={() => {
                            setCart( (prevCart) => ({...prevCart, showCart : !cart.showCart}) );
                            setBlur(false);
                        }}>
                            <div>
                                <p>{cart.cartQuantity}</p>
                            </div>
                            <p>Cart</p>
                        </button>
                    </div>
                </nav>
            </header>
            {blur ? <LoginSection /> : null}
            {cart.showCart ? <ShowCartList /> : null}
        </>
    )
}

export default Navigation;
