import Nav from '../../../src/components/navigation'
import Footer from '../../components/footer';
import './index.css'
import { useState, useEffect } from 'react';
import AOS from 'aos';
import 'aos/dist/aos.css';

import { useDispatch, useSelector } from 'react-redux';
import { SetMobile } from '../../features/events-control/eventsControlSlice';

class linkButton {
    constructor(routeId, name, img, redirect) {
        this.routeId = routeId;
        this.name = name;
        this.img = img;
        this.redirect = redirect;
    }
}

let buttonList = [];
//process.env.PUBLIC_URL + '/navigation-images/user-profile-icon.png'
buttonList.push(new linkButton('/login', <p>Account</p>, null, false))
buttonList.push(new linkButton('/menu', <p>Menu</p>, null, true))
buttonList.push(new linkButton('/', <p>Stores</p>, null, false))
buttonList.push(new linkButton('/', <p>Promotions</p>, null, false))

const LandingPage = () => {
    AOS.init();
    const dispatch = useDispatch();

    const [windowHeight, setWindowHeight] = useState(-50);

    const setHeight = () => {
        setWindowHeight(window.scrollY - document.getElementById('vid').offsetHeight);
    }

    useEffect(() => {
        if (window.innerWidth < 800) {
            dispatch(SetMobile(true))
            setVideo('/navigation-images/principal-bg-mobile.png');
        }
        else if (window.innerWidth > 800) {
            dispatch(SetMobile(false))
            setVideo('/navigation-images/principal-bg.webp')
        }
    }, [dispatch])

    const { isOnMobile } = useSelector(state => state.controlEvents)

    useEffect(() => {
        document.addEventListener("scroll", setHeight);

        const videoSetter = () => {
            if (window.innerWidth < 800 && !isOnMobile) {
                dispatch(SetMobile(true))
                setVideo('/navigation-images/principal-bg-mobile.png');
            }
            else if (window.innerWidth > 800 && isOnMobile) {
                dispatch(SetMobile(false))
                setVideo('/navigation-images/principal-bg.webp')
            }
        }

        window.addEventListener("resize", videoSetter);

        return () => {
            document.removeEventListener("scroll", setHeight)
            window.removeEventListener("resize", videoSetter);
        }
    })

    let [video, setVideo] = useState('');

    let [hovering, setHovering] = useState(false);

    const [text, setText] = useState('');

    return (
        <>
            <Nav button={buttonList} windowHeight={windowHeight} isOnMobile={isOnMobile} />
            <main>
                <div className='video-banner-landing'>
                    <img id='vid' alt='principal bg' preload='none'
                        src={process.env.PUBLIC_URL + video}>
                    </img>
                    <div className='inside-video'>
                        <h1>Pizza is <p>HERE<span>!</span></p></h1>
                        <button onClick={() => {
                            document.getElementById('ad0').scrollIntoView({ behavior: "smooth", block: "end", inline: "nearest" })
                        }}>See our promotions</button>
                    </div>
                </div>
                <section id='buttons-banner'>
                    <div id='flexb'>
                        <hr />
                        <p>Choose your option</p>
                        <div>
                            <button className='radius-left'
                                onMouseOver={() => { setHovering(true);
                                    setText('O delivery da nossa pizzaria é uma ótima opção para quem quer comer pizza sem sair de casa. Com o delivery, você pode pedir suas pizzas favoritas e recebê-las no conforto da sua casa. Para fazer um pedido pelo delivery, basta acessar o nosso site e escolher as pizzas que deseja. Temos uma grande variedade de sabores para você escolher, desde as tradicionais até as mais exóticas. Além disso, oferecemos diversas opções de pagamento para facilitar a sua vida. Você pode pagar com cartão de crédito, débito ou dinheiro na entrega. O tempo médio de entrega é de 30 a 45 minutos, dependendo da sua localização. Verifique a área de cobertura do nosso serviço para saber se atendemos a sua região. Não perca mais tempo e peça agora mesmo as suas pizzas favoritas pelo delivery da nossa pizzaria!'); }}
                                onMouseOut={() => { setHovering(false) }}>Delivery</button>
                            <div className='bar-between'>
                                <hr />
                            </div>
                            <button className='radius-right'
                                onMouseOver={() => { setHovering(true); setText('Buscar o produto pessoalmente em nossa loja tem muitas vantagens. Além de poder ver e experimentar o produto antes de comprá-lo, você economiza no frete e pode retirar o produto na hora. Nossa loja está localizada em um lugar de fácil acesso e com estacionamento gratuito para clientes. Além disso, temos uma equipe de atendimento pronta para ajudá-lo a escolher o produto ideal para suas necessidades. Ao buscar o produto pessoalmente, você também pode aproveitar para conhecer outros produtos que temos em nossa loja e tirar suas dúvidas com nossa equipe de atendimento. Não perca mais tempo e venha nos visitar! Estamos ansiosos para recebê-lo em nossa loja.'); }}
                                onMouseOut={() => { setHovering(false) }}>Local</button>
                        </div>
                        <hr />
                    </div>
                    <div id='info-text'>
                        {hovering ? <p data-aos="zoom-in">{text}</p> : null}
                    </div>
                </section>
                {/* <h2 id='promotions-h2'>Promotions Of The Day</h2>
                <hr id='promo-hr'/>
                <section className='delivery-section'>
                    <div id='delivery-info'>
                        <h1>Delivery it self</h1>
                        <p>Has to go to your house</p>
                    </div>
                    <div id='delivery-opt'>
                        <h1>Options of delivery</h1>
                        <p>1 2 3</p>
                    </div>
                </section> */}
                {/* <div className='images-container'>
                    <img id='ad0' src='' alt='ad'></img>
                    <aside className='aside-image-container'>
                        <img id='ad1' src='' alt='ad'></img>
                        <img id='ad2' src='' alt='ad'></img>
                    </aside>
                    <img id='ad3' src='' alt='ad'></img>
                </div> */}
            </main>
            <footer>
                <Footer />
            </footer>
        </>
    )
}

export default LandingPage;
