// NODE MODULES
import React, { useEffect } from 'react';
import { useState } from 'react';
import './index.css';
import { useNavigate } from 'react-router-dom';
// MY COMPONENTS
import LoginSection from '../login-section';
import ShowCartList from '../show-cart-list';
import BlurConfig from './blur-config';
import Menu from './menu-config';

const Navigation = (props) => {
    const [test, setTest] = useState(-50);

    const scrollEvent = () => {
        console.log('TENTANDO PEGAR ALTURA')
        setTest(window.scrollY - document.getElementById('vid').offsetHeight)
    }

    useEffect(() => {
        console.log(props.useScrollE)
        if(props.useScrollE === true){
            window.addEventListener("scroll", scrollEvent);
            console.log('evento adicionado')
        }
        else{
            window.removeEventListener("scroll", scrollEvent)
            setTest(0)
            console.log('evento removido')
        }
    }, [props.useScrollE])
    
    /* Direct configs */
    let navigate = useNavigate();
    const directTo = (id) => {
        navigate(`${id}`);
    }

    /* Login configs */
    let [isLoginOn, setLogin] = useState(false);

    /* Cart configs */
    let [cart, setCart] = useState({
        showCart: false,
        cartQuantity: 0
    });
    const updateCart = (newValue) => {
        setCart((prevCart) => ({...prevCart, showCart : newValue}))
    }
    if(cart.showCart || isLoginOn){
        document.querySelector('body').classList.add('hide-scroll')
    }
    else{
        document.querySelector('body').classList.remove('hide-scroll')
    }

    return (
        <> {/* Principal Return */}
            <header className={test > -40 ? 'solid-header' : 'transparent-header'}>
                <nav>
                    <div className='main-nav'>
                        <img className='logo-img' src={process.env.PUBLIC_URL + '/navigation-images/logo.png'} alt='Site Logo' onClick={() => directTo('/')}></img>
                        <Menu/>
                    </div>
                    <div className='sub-nav display-none'>
                        <div className='create-button'>
                            {
                                props.button.map((element, i) => {
                                    return (
                                        <React.Fragment key={i}>
                                            <button className={test > -40 ? 'black-letter' : 'white-letter'} onClick={() => {
                                                setCart( (prevCart) => ({...prevCart, showCart : false}) );
                                                
                                                if(element.routeId === '/login')
                                                    setLogin(!isLoginOn);
                                                
                                                return element.redirect ? directTo(`${element.routeId}`) : null
                                            }}>
                                                {element.name}
                                            </button>
                                        </React.Fragment>
                                    )
                                })
                            }
                        </div>
                        <div className='cart-button'>
                            <button onClick={() => {
                                setCart( (prevCart) => ({...prevCart, showCart : !cart.showCart}) );
                                setLogin(false);
                            }}>
                                <div>
                                    <p>{cart.cartQuantity}</p>
                                </div>
                                <p>Cart</p>
                            </button>
                        </div>
                    </div>
                </nav>
                {props.extraNav != null ? <>{props.extraNav}</> : null}
                {test > -40 ? null : <hr/>}
            </header>
            
            {/* Blur the page when log in */}
            <BlurConfig loginState={isLoginOn}/>

            {/* Show login & cart configs */}
            {isLoginOn ? <LoginSection setLogin={(newValue) => {
                setLogin(newValue)
            }}/> : null}
            {cart.showCart ? <ShowCartList quant={cart.cartQuantity} 
            updateCart={updateCart} cartState={cart.showCart} /> : null}
        </>
    )
}

export default Navigation;
